import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import ReactMarkdown from 'react-markdown';
import styled from 'styled-components';
import matter from 'gray-matter';

const PostContainer = styled.article`
  padding: ${props => props.theme.spacing.medium};
  max-width: 800px;
  margin: 0 auto;

  h1, h2, h3, h4, h5, h6 {
    color: ${props => props.theme.colors.primary};
    margin-top: ${props => props.theme.spacing.large};
    margin-bottom: ${props => props.theme.spacing.medium};
  }

  p {
    line-height: ${props => props.theme.typography.lineHeight};
    margin-bottom: ${props => props.theme.spacing.medium};
  }

  code {
    background-color: #f5f5f5;
    padding: 0.2em 0.4em;
    border-radius: 3px;
  }

  pre {
    background-color: #f5f5f5;
    padding: ${props => props.theme.spacing.medium};
    border-radius: 4px;
    overflow-x: auto;
  }

  img {
    max-width: 100%;
    height: auto;
  }

  blockquote {
    border-left: 4px solid ${props => props.theme.colors.secondary};
    margin: 0;
    padding-left: ${props => props.theme.spacing.medium};
    color: #666;
  }
`;

const PostHeader = styled.div`
  margin-bottom: ${props => props.theme.spacing.large};
`;

const PostTitle = styled.h1`
  color: ${props => props.theme.colors.primary};
  margin-bottom: ${props => props.theme.spacing.small};
`;

const PostDate = styled.div`
  color: ${props => props.theme.colors.secondary};
  font-size: 0.9rem;
`;

const PostDescription = styled.div`
  color: ${props => props.theme.colors.text};
  font-style: italic;
  margin-top: ${props => props.theme.spacing.small};
`;

const LoadingMessage = styled.div`
  text-align: center;
  padding: ${props => props.theme.spacing.large};
  color: ${props => props.theme.colors.secondary};
`;

const ErrorMessage = styled.div`
  text-align: center;
  padding: ${props => props.theme.spacing.large};
  color: ${props => props.theme.colors.accent};
`;

function BlogPost() {
  const { id } = useParams();
  const [post, setPost] = useState({
    content: '',
    title: '',
    date: '',
    description: ''
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    setLoading(true);
    setError(null);

    const fetchPost = async () => {
      try {
        // First check if the post exists in the index
        const indexResponse = await fetch('/posts/index.json');
        if (!indexResponse.ok) {
          throw new Error('Failed to load posts index');
        }
        const { posts } = await indexResponse.json();
        const postInfo = posts.find(p => p.id === id);

        if (!postInfo) {
          setError('Post not found');
          return;
        }

        const response = await fetch(`/posts/${postInfo.file}`);
        if (!response.ok) {
          throw new Error('Failed to load post content');
        }

        const text = await response.text();
        const { data, content } = matter(text);

        setPost({
          content,
          title: data.title,
          date: data.date,
          description: data.description
        });
      } catch (error) {
        console.error('Error loading post:', error);
        setError('Failed to load the post. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchPost();
  }, [id]);

  if (loading) {
    return <LoadingMessage>Loading post...</LoadingMessage>;
  }

  if (error) {
    return <ErrorMessage>{error}</ErrorMessage>;
  }

  return (
    <PostContainer>
      <PostHeader>
        <PostTitle>{post.title}</PostTitle>
        {post.date && <PostDate>{post.date}</PostDate>}
        {post.description && <PostDescription>{post.description}</PostDescription>}
      </PostHeader>
      <ReactMarkdown>{post.content}</ReactMarkdown>
    </PostContainer>
  );
}

export default BlogPost;
