import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';

const PostList = styled.div`
  display: grid;
  gap: ${props => props.theme.spacing.large};
`;

const PostCard = styled(Link)`
  padding: ${props => props.theme.spacing.medium};
  border: 1px solid ${props => props.theme.colors.primary};
  border-radius: 4px;
  text-decoration: none;
  color: ${props => props.theme.colors.text};
  transition: transform 0.2s, box-shadow 0.2s;
  display: block;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
    border-color: ${props => props.theme.colors.secondary};
  }
`;

const PostTitle = styled.h2`
  margin: 0;
  color: ${props => props.theme.colors.primary};
  font-size: 1.5rem;
`;

const PostDate = styled.p`
  color: ${props => props.theme.colors.secondary};
  margin: ${props => props.theme.spacing.small} 0;
  font-size: 0.9rem;
`;

const LoadingMessage = styled.div`
  text-align: center;
  padding: ${props => props.theme.spacing.large};
  color: ${props => props.theme.colors.secondary};
`;

const ErrorMessage = styled.div`
  text-align: center;
  padding: ${props => props.theme.spacing.large};
  color: ${props => props.theme.colors.accent};
`;

function BlogList() {
  // Static list of posts for now
  const posts = [
    {
      id: 'welcome',
      title: 'Welcome to My Markdown Blog',
      date: '2025-02-21',
      description: 'Introduction to our Markdown-powered blog system'
    },
    {
      id: 'markdown-guide',
      title: 'Markdown Guide - Tips and Tricks',
      date: '2025-02-21',
      description: 'Learn how to use Markdown features to create beautiful blog posts'
    }
  ];

  return (
    <PostList>
      {posts.map(post => (
        <PostCard key={post.id} to={`/post/${post.id}`}>
          <PostTitle>{post.title}</PostTitle>
          <PostDate>{post.date}</PostDate>
          <p>{post.description}</p>
        </PostCard>
      ))}
    </PostList>
  );
}

export default BlogList;
