import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import matter from 'gray-matter';

const PostList = styled.div`
  display: grid;
  gap: ${props => props.theme.spacing.large};
`;

const PostCard = styled(Link)`
  padding: ${props => props.theme.spacing.medium};
  border: 1px solid ${props => props.theme.colors.primary};
  border-radius: 4px;
  text-decoration: none;
  color: ${props => props.theme.colors.text};
  transition: transform 0.2s, box-shadow 0.2s;
  display: block;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
    border-color: ${props => props.theme.colors.secondary};
  }
`;

const PostTitle = styled.h2`
  margin: 0;
  color: ${props => props.theme.colors.primary};
  font-size: 1.5rem;
`;

const PostDate = styled.p`
  color: ${props => props.theme.colors.secondary};
  margin: ${props => props.theme.spacing.small} 0;
  font-size: 0.9rem;
`;

const LoadingMessage = styled.div`
  text-align: center;
  padding: ${props => props.theme.spacing.large};
  color: ${props => props.theme.colors.secondary};
`;

const ErrorMessage = styled.div`
  text-align: center;
  padding: ${props => props.theme.spacing.large};
  color: ${props => props.theme.colors.accent};
`;

function BlogList() {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    setLoading(true);
    setError(null);

    const loadPosts = async () => {
      try {
        // First, fetch the posts index
        const indexResponse = await fetch('/posts/index.json');
        if (!indexResponse.ok) {
          throw new Error('Failed to load posts index');
        }
        const { posts: fileList } = await indexResponse.json();

        const loadedPosts = await Promise.all(
          fileList.map(async ({ id, file }) => {
            try {
              const response = await fetch(`/posts/${file}`);
              if (!response.ok) {
                throw new Error(`Failed to load post: ${file}`);
              }
              const text = await response.text();
              const { data } = matter(text);

              return {
                id,
                title: data.title,
                date: data.date,
                description: data.description
              };
            } catch (error) {
              console.error(`Error loading ${file}:`, error);
              return null;
            }
          })
        );

        // Filter out any null entries and sort by date
        const validPosts = loadedPosts
          .filter(post => post !== null)
          .sort((a, b) => new Date(b.date) - new Date(a.date));

        setPosts(validPosts);
      } catch (error) {
        console.error('Error loading posts:', error);
        setError('Failed to load blog posts. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    loadPosts();
  }, []);

  if (loading) {
    return <LoadingMessage>Loading posts...</LoadingMessage>;
  }

  if (error) {
    return <ErrorMessage>{error}</ErrorMessage>;
  }

  if (posts.length === 0) {
    return <LoadingMessage>No posts found.</LoadingMessage>;
  }

  return (
    <PostList>
      {posts.map(post => (
        <PostCard key={post.id} to={`/post/${post.id}`}>
          <PostTitle>{post.title}</PostTitle>
          <PostDate>{post.date}</PostDate>
          <p>{post.description}</p>
        </PostCard>
      ))}
    </PostList>
  );
}

export default BlogList;
